  display: flex;
  justify-content: center;
  line-height: 5vh;
  margin: 0;
}

nav li {
  list-style: none;
  margin: 0 40px;
}

nav a {
  color: white;
  padding: 10px 20px;
  text-decoration: none;
}

nav a:hover {
  border-radius: 5px;
  background: #0491d2;
  box-shadow: inset 20px 20px 40px #037bb3, inset -20px -20px 40px #05a7f2;
}

header {
  height: 95vh;
  margin-top: 5vh;
}

header .content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  height: 95vh;
}

header h1,
header h2 {
  color: #f5d12f;
  font-family: 'Russo One', sans-serif;
}

header h1 {
  font-size: 60px;
  text-shadow: rgba(255, 255, 255, 0.15) 5px 5px 0px;
}

header h2 {
  font-size: 36px;
  text-align: center;
}

#heroes {
  position: relative;
  height: 45vh;
}

@keyframes floatImg {
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-20px);
  }
  100% {
    transform: translateY(0);
  }
}

#heroes img {
  position: absolute;
  left: 50%;
  top: -200px;
  margin-left: -375px;
  width: 750px;
  animation: floatImg 2s infinite linear;
  background: url(/659b95411cea5979dc8d1429cb7b1e40.png);
  background-size: contain;
  background-repeat: no-repeat;
}

section {
  padding: 50px 10vw;
  margin: 0 auto;
}

section h2,
section h3,
section strong .button {
  text-shadow: 0px 1px 1px #3338;
}

